import React from "react";

import sortAscImg from './assets/img/sort-asc.svg';
import sortDescImg from './assets/img/sort-desc.svg';
import unsortedImg from './assets/img/unsorted.svg';

const Columns = ({ columns, currentSortedCol, handleSortColumn }) => {
  return (
    <thead>
      <tr>
        {columns.map((col, i) => {
          let nextSort = 1;
          let iconSorted = unsortedImg;
          if (i === currentSortedCol.idx) {
            if (currentSortedCol.sortedBy === 1) {
              iconSorted = sortAscImg;
              nextSort = -1;
            } else if (currentSortedCol.sortedBy === -1) {
              iconSorted = sortDescImg;
            }
          }

          return (
            <th key={i} onClick={() => handleSortColumn(i, nextSort)}
              style={{ cursor: 'pointer' }}
            >
              {col.title}{" "}
              <img src={iconSorted} alt={`Sort ${col.title}`} width="16px" />
            </th>
          )
        })}
      </tr>
    </thead>
  );
};

export default Columns;
