import React from "react";

const Counting = ({ currentPage, pageSize, currentMax }) => {
  const from = (currentPage - 1) * pageSize + 1;
  let to = currentPage * pageSize;

  if (to > currentMax) {
    to = currentMax;
  }

  return (
    <p className="mt-1">
      Showing {from} to {to} of {currentMax} entries
    </p>
  );
};

export default Counting;
