import React from "react";

import { Form, FormGroup, Label, Input } from "reactstrap";

const Filter = ({ filter, handleFilter }) => (
  <Form inline>
    <FormGroup>
      <Label>
        Filter:&nbsp;
          <Input
          type="text"
          value={filter}
          onChange={(e) => handleFilter(e.target.value || "")}
          bsSize="sm"
        />
      </Label>
    </FormGroup>
  </Form>
);

export default Filter;